document.addEventListener("DOMContentLoaded",function(){var o=new ZeroClipboard($(".click-to-copy")),t=["Good Job","You Suck","Holy Shit","Oh Fuck","Hell Yeah","Dammn.."];o.on("ready",function(){o.on("aftercopy",function(o){var n=o.data["text/plain"];swal({title:t[Math.floor(Math.random()*t.length)]+"!",text:"Go forth and design with "+n,type:"success",confirmButtonText:"Cool",confirmButtonColor:n,allowOutsideClick:!0})})}),o.on("error",function(){ZeroClipboard.destroy()})});